/* based on my shader : https://www.shadertoy.com/view/4dSBW1 */

void mainImage( out vec4 f, vec2 g )
{
    vec3 
        s = iResolution,
        r = vec3(g+g-s.xy,s.y),
    	k=s+iTime,
        q;

    k.z = iTime;

	float d,u,c,i;

    for(d=u=i=0.;i<1.;i+=.002)
	{      
        // the normalize here is costly but just for have less chars :)
		q = k + r/length(r) * d * .3;

        c = length(texture(iChannel0, (q+sin(q.zxy*2.))*.05)) - 1.01;

        if(u>.95) break;

        if (c < .1) u += (.1-c) * i; 					

        u += .002; 										

        d += max(c * .8, .06);							
   	}

	f -= f - exp(-d*d/1e3)/i/7.;
}
