float plane(vec3 pos) {
    return pos.y;
}

float sphere(vec3 pos, float radius) {
    pos = mod(pos + 2.0, 4.0) - 2.;
    return length(pos) - radius;
}

float noise(vec3 pos, float radius) {
    return length(texture(iChannel0, (pos.xy * (.1 + sin(iTime) * .05)) + vec2(0., sin(iTime) * -0.1)).rgb) * .125;
}

float map(vec3 pos) {
 return pos.y;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;

    uv.x *= iResolution.x / iResolution.y;

    vec3 pos = vec3(0., 0., 0.);
    vec3 startPos = pos;
    vec3 dir = normalize(vec3(uv, 1.0));

    vec3 color = vec3(0.0);
    float dist = 0.0;

    for(int i = 0; i < 32; i++) {
       float d = noise(pos, 1.0);
        if(d < 0.01) {
            color = fract(pos);
            break;
        }
        pos += d * dir;
    }

    dist = length(pos - startPos);

    float fog = 4.0 / ( pow(dist, 2.0) ) * 8.0 - 2.1;

    vec4 tc = texture(iChannel0, vec2(dist) * 2. + iTime);

	fragColor = vec4(vec3(fog) * dot(tc.rgb, vec3(1.1)), 1.0);
}
