float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = vec4( 
        			// union
        			vec3( 255.0,255.0, 0.0 )*(
        			1.0-(min(
                        // sphere, center 150,200 with radius 40
                        (sdSphere( vec3( fragCoord.x-150.0 , fragCoord.y-200.0 , 1.0), 40.0 )) ,

                        // sphere, center 170,180 with radius 30
        				(sdSphere( vec3( (fragCoord.x- (iTime*10.0) ) -50.0 , fragCoord.y-180.0 , 1.0), 30.0 ))
                        ))
                        ),
        			1.0);
}
