void mainImage( out vec4 O, vec2 U )
{
    float N = 2.+mod(floor(iTime),6.), // number of axis
          S = 8.,                      // scale
          a = 3.14/N, 
          h = iResolution.y;
    U /= h; U -= .5;
    O -= O;

    for (float i=0.; i<N; i++)
        O += smoothstep(S/h, 0., abs(fract(S*U.x)-.5)-.1), // * (.7+.3*sin(6.28*S*U.y)),
        U *= mat2(cos(a),-sin(a),sin(a),cos(a));

    O *= vec4(1,.9,.8,0);
}
