#define PI	3.14159
#define TAU 6.28318

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord / iResolution.y;

    vec4 m = vec4(
        0.5 + 0.5 * sin(0.5 * PI * (iMouse.xy / iResolution.xy - 0.5)),
        0.5 + 0.5 * sin(0.5 * PI * (iMouse.zw / iResolution.xy - 0.5))
    );

    float tt = (floor(iTime * 0.2) * 50.0) + iTime;

    vec3 t = vec3(
        tt + 0.00,
        tt + 0.02,
        tt + 0.05
    );

    vec3 a = vec3(abs(
        cos(PI * uv.x * 3.0 + m.x * 3.0) +
        sin(PI * uv.y * 3.0 + m.y * 3.0) +
        0.3 * sin(PI * uv.y * 4.0 + t * 0.5) +
        sin(t * 0.75)
    ) * 0.4);

   	vec3 b = abs(abs(
            sin(PI * uv.x * 2.0 + t * 0.07 - m.x * 1.0) +
            cos(PI * uv.y * 2.0 + t * 0.09 - m.y * 1.0)
        ) * 0.5 - (0.6 + 0.4 * sin(2.0 * uv.x * uv.y + t))
    );

    vec3 c = max(sin(a), cos(b - a));
    c = c * 0.7 + 0.3 * (m.x * c + m.y * a + (1.0 - m.x) * b + (1.0 - m.y) * (b - a));

    vec3 d1 = sin(
        0.5 * PI *
        pow(
            abs(0.1 + 0.07 * sin(t * 0.5)),
            abs(sin(b + c + t + uv.y * 0.2))
        )
    );
    vec3 d2 = sin(
        0.5 * PI * cos(max(b, c - a * 0.1)) * 
        pow(
            abs(0.1 + 0.07 * sin(t * 0.5)),
            abs(sin(b + c * m.y + uv.y * 0.2 + 0.1 * sin(t))) *
            abs(cos(a - c * m.x + uv.x * 0.2))
        )
    );
    vec3 d = 0.1 * d1 + 0.9 * d2;

    vec3 e = d + smoothstep(vec3(0.0), a + b + d, d);

    vec3 f = vec3(
        e.r + a.r * 0.03 + c.b * 0.08 * sin(tt * 0.3 + 1.0),
    	e.g + e.r * 0.067 + c.r * 0.05 * sin(tt * 0.23 + 0.5),
    	e.b - e.b * 0.3 * max(e.r, e.g) * (1.0 - e.b) + c.g * 0.1 * sin(tt * 0.4 + 1.5)
    ) * 0.9;

    f.r = f.r * 0.8 + f.g * 0.3;
    f.g = f.g * 0.9 + f.b * 0.2;

	fragColor = vec4(abs(f * 0.6), 1.0);
}
