// Apparently you need to click in the view first.
// Use mouse to control stuff.

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float fas(float f) {
    // return fract(abs(sin(itime)*127455.0*iMouse.xy.x+1.0));
    //return fract(abs(sin(itime*iMouse.xy.y)));

    return fract(abs(sin(f)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = fragCoord.xy/iResolution.xy;
    vec2 m = max(iMouse.xy, 0.1)/iResolution.xy;    
    vec4 audio = texture(iChannel0, p);

    // Controls
    float ca = audio.x/m.y;
    float ct = iTime/m.x;

    float xf = (ca); // / (ft);
    float yf = (ct*ca);

    float r = fas(p.x*xf);
    float g = fas(p.y*yf);
    float b = fas((p.x*p.y*xf*yf));

    fragColor = vec4(r,g,b, 1.0);
}
