void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float val = texture(iChannel0, uv).x;
    fragColor = vec4(val*val,val*val, val*val , 1);   
}

// Cubes: k1=1, k2=6, g=100, max_state=500
// Spirals: k1=3, k2=3, g=90, max_state=500
// Spirals with dirt: k1=6, k2=1, g=100, max_state=500
// Noise: k1=3, k2=4, g=100, max_state=500
// Oscilatinig patterns: k1=3, k2=3, g=56, max_state=500

const float k1 = 3.; 
const float k2 = 3.;
const float g = 90.;
const float max_state = 500.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 xy = fragCoord.xy / iResolution.xy;
	vec2 texel = 1. / iResolution.xy;

    float delta_x = texel.x;
    float delta_y = texel.y;

    vec2 n = vec2(0.0, delta_y);
    vec2 ne = vec2(delta_x, delta_y);
    vec2 e = vec2(delta_x, 0.0);
    vec2 se = vec2(delta_x, -delta_y);
    vec2 s = vec2(0.0, -delta_y);
    vec2 sw = vec2(-delta_x, -delta_y);
    vec2 w = vec2(-delta_x, 0.0);
    vec2 nw = vec2(-delta_x, delta_y);

    vec3 uv =    texture(iChannel0, xy).xyz;
    vec3 uv_n =  texture(iChannel0, xy+n).xyz;
    vec3 uv_e =  texture(iChannel0, xy+e).xyz;
    vec3 uv_s =  texture(iChannel0, xy+s).xyz;
    vec3 uv_w =  texture(iChannel0, xy+w).xyz;
    vec3 uv_nw = texture(iChannel0, xy+nw).xyz;
    vec3 uv_sw = texture(iChannel0, xy+sw).xyz;
    vec3 uv_ne = texture(iChannel0, xy+ne).xyz;
    vec3 uv_se = texture(iChannel0, xy+se).xyz;

    if (uv.x > 0.99)
        uv.x = 0.;

    else{
        int infected = 0;
        int sick = 0;

        if (uv_n.x > 0.99) sick ++;
        else if (uv_n.x > 0.) infected ++;
        if (uv_e.x > 0.99) sick ++;
        else if (uv_e.x > 0.) infected ++;
        if (uv_s.x > 0.99) sick ++;
        else if (uv_s.x > 0.) infected ++;
        if (uv_w.x > 0.99) sick ++;
        else if (uv_w.x > 0.) infected ++;
        if (uv_nw.x > 0.99) sick ++;
        else if (uv_nw.x > 0.) infected ++;
        if (uv_ne.x > 0.99) sick ++;
        else if (uv_ne.x > 0.) infected ++;
        if (uv_sw.x > 0.99) sick ++;
        else if (uv_sw.x > 0.) infected ++;
        if (uv_se.x > 0.99) sick ++;
        else if (uv_se.x > 0.) infected ++;

        if (uv.x == 0.){
            uv.x = floor(float(infected) / k1) + floor(float(sick) / k2);
        }
        else{
            float sum = (uv_n.x + uv_s.x + uv_e.x + uv_w.x + uv_nw.x + uv_ne.x + uv_sw.x + uv_se.x) * max_state;
            uv.x = sum / (float(infected + sick) + 1.) + g;
        }

        if (uv.x > max_state)
            uv.x = max_state;
    }


    if(iFrame<5) {
        fragColor = texture(iChannel1, fragCoord.xy / iResolution.xy);
    } else {
        fragColor = vec4(uv.x / max_state);
    }

}
