void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //rows & cols
    float rows = 5.0;
    float cols = 5.0;
    //normalized screen
    vec2 uv = fragCoord.xy / iResolution.xy;
    //set up colors for squares
    vec4 color1 = vec4(0.0, 1.0, 0.0, 1.0);
    vec4 color2 = vec4(0.0, 0.0, 1.0, 1.0);

    //bool for whether to use textures or not
    bool useTextures = true;

    //Set up a grid. Each square is centered at uv.x/uv.y
    //times the number of rows or columns, rounded down. 
    vec2 grid = vec2(floor(uv.x * rows), floor(uv.y*cols));

    //Place the squares. If the x and the y of the square are 
    //divisible by 2, then the square is placed.
    if(mod(grid.x + mod(grid.y, 2.0), 2.0) == 0.0)
        fragColor = color1;
    else
        fragColor = color2;

	//bonus?
    if(useTextures)
    {
        //Set up the textures
    	color1 = texture(iChannel0, uv);
        color2 = texture(iChannel1, uv);

        //Blend the colors?
        vec4 color3 = vec4(color1.r, color2.g, color2.b, 1.0);
        vec4 color4 = vec4(color2.r, color1.g, color1.b, 1.0);

        //Place the squares
        if(mod(grid.x + mod(grid.y, 2.0), 2.0) == 0.0)
            fragColor = color3;
        else
            fragColor = color4;

    }

}
