mat3 getCamera(vec3 z) {
	vec3 x = normalize(cross(vec3(0.0, 1.0, 0.0), z));
    return mat3(x, cross(z, x), z);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 mo = iMouse.xy / iResolution.y * 5.0;
    vec3 ro = vec3(sin(mo.x), 0.0, cos(mo.x));
    vec3 rd = getCamera(normalize(ro)) * vec3(uv, -1.0);
    fragColor = vec4(texture(iChannel0, rd).xyz, 1.0);
}
