// compressed from https://www.shadertoy.com/view/4ldGDM
// cousin quadramaze: https://www.shadertoy.com/view/4scGWf

void mainImage( out vec4 O, vec2 U )
{                                                // conversion to hexagonal coordinates   
    O  += .1 / fract( (O.a= sin( 1e5* length( ceil( U *= mat2(12, -7, 0, 14) / 1e2 ))))
                           > .5  ? U : O.a < -.5 ? U.yx : U+U.y ).x - O; 
}

