/**   // 133 chars

void mainImage( out vec4 O, vec2 U )
{                               // conversion to hexagonal coordinates   
    float r = sin( 1e5* length( ceil( U *= mat2(12, -7, 0, 14) / 1e2 )));
    O  += .1 / fract( r > .5  ? U.x : r < -.5 ? U.y : -U.x-U.y ) - O; 
}

