void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec4 colPrev = texelFetch(iChannel0, ivec2(fragCoord.x, fragCoord.y), 0);

    float rectSize = abs(sin(iTime)) * .6;

    vec2 center = vec2(0);
    vec2 distanceFromCenter = abs(uv - center);

    if(rectSize > distanceFromCenter.x && rectSize > distanceFromCenter.y) {
        colPrev += vec4((uv.x * .5 + .5)*.001, (uv.y * .5 + .5)*.002, (uv.x * .5 + .5)*.003, 0);
    }

    fragColor = colPrev;
}
