float shp( vec2 p, vec2 c )
{
    float r = 1.8;
   	float v = max( r - length( p - c ), 0.0 );

    v = pow( v, 5.4 );
    //
   	return v;
}


float circle( vec2 c, vec2 p, float r )
{
    float d = distance( c, p );

    float v = 1.0 - ( r - d );

    v = pow( v, 12.54 );

    return v;
}

vec2 rotate( vec2 p, float a )
{
    return mat2( cos( a ), -sin( a ),
                 sin( a ),  cos( a ) ) * p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float a = iResolution.x / iResolution.y;

    vec2 p = uv * 2.0 - 1.0;
    p.x *= a;

    vec2 center = ( iMouse.xy / iResolution.xy ) * 2.0 - 1.0;
    center.x *= a;

    float m = shp( p * 2.8, center * 2.8 );
   	vec2 texCoord = uv + m * rotate( 2.0 * uv - 1.0, iTime * 1.8 );

    vec3 col = texture( iChannel1, texCoord ).xyz;
    col *= clamp( circle( center, p, 0.45 ), 0.0, 1.0 );


    fragColor = vec4(col,1.0);
}
