struct Shape{
  float dist;
  vec4 color;
};

//=======================
// Utility Functions
//=======================
float random(vec2 v) {
  return fract(sin(dot(v*0.1, vec2(324.654, 156.546)))*46556.2);
}

mat2 rot(float a){
  float r = cos(a);
  float f = sin(a);
  return mat2(r, f, -f, r);
}
// Cylinder standing upright on the xz plane
float fCylinder(vec3 p, float r, float h) {
    float d = length(p.xz) - r;
    d = max(d, abs(p.y) - h);
    return d;
}
float fCone(vec3 p, float radius, float height) {
	vec2 q = vec2(length(p.xz), p.y);
	vec2 tip = q - vec2(0, height);
	vec2 mantleDir = normalize(vec2(height, radius));
	float mantle = dot(tip, mantleDir);
	float d = max(mantle, -q.y);
	float projected = dot(tip, vec2(mantleDir.y, -mantleDir.x));

	// distance to tip
	if ((q.y > height) && (projected < 0.)) {
		d = max(d, length(tip));
	}

	// distance to base ring
	if ((q.x > radius) && (projected > length(vec2(height, radius)))) {
		d = max(d, length(q - vec2(radius, 0)));
	}
	return d;
}


float vmax(vec3 v) {
	return max(max(v.x, v.y), v.z);
}

// Box: correct distance to corners
float fBox(vec3 p, vec3 b) {
	vec3 d = abs(p) - b;
	return length(max(d, vec3(0))) + vmax(min(d, vec3(0)));
}



float mixColors(float r, float v, float z){
  return clamp(0.5+0.5*(v-r)/z, 0., 1.);
}

float mixShapes(float v, float f, float r){
  float z = mixColors(v, f, r);
  return mix(f,v,z)-r*z*(1.-z);
}

float opS( float d1, float d2 )
{
    return max(-d1,d2);
}

float pModPolar(inout vec2 v, float r){
  float f = 6.28318/r;
  float z = atan(v.y, v.x)+f*0.5;
  float m = floor(z/f);
  z = mod(z, f)-f*0.5;
  v = vec2(cos(z), sin(z))*length(v);
  return m;
}

void pR45(inout vec2 p) {
	p = (p + vec2(p.y, -p.x))*sqrt(0.5);
}

float pMod1(inout float p, float size) {
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

float fOpUnionColumns(float a, float b, float r, float n) {
	if ((a < r) && (b < r)) {
		vec2 p = vec2(a, b);
		float columnradius = r*sqrt(2.)/((n-1.)*2.+sqrt(2.));
		pR45(p);
		p.x -= sqrt(2.)/2.*r;
		p.x += columnradius*sqrt(2.);
		if (mod(n,2.) == 1.) {
			p.y += columnradius;
		}
		// At this point, we have turned 45 degrees and moved at a point on the
		// diagonal that we want to place the columns on.
		// Now, repeat the domain along this direction and place a circle.
		pMod1(p.y, columnradius*2.);
		float result = length(p) - columnradius;
		result = min(result, p.x);
		result = min(result, a);
		return min(result, b);
	} else {
		return min(a, b);
	}
}

float fOpUnionStairs(float a, float b, float r, float n) {
	float s = r/n;
	float u = b-r;
	return min(min(a,b), 0.5 * (u + a + abs ((mod (u - a + s, 2.* s)) - s)));
}

//=======================

Shape environment(vec3 c){
  Shape shape;
  shape.dist = 1000.; // Draw Distance
  shape.color = vec4(1.); // Initial Color

  // Coordinate Systems
  vec3 g = c; // Ground
  vec3 l = c; // light
  vec3 p = c; // Pillars
  vec3 s = c; // Side

  // Ground
  vec4 gColor = vec4(1., 0.0, 0.4, 0.0);
  g.xy *= rot(radians(90.)); 
  g.y = abs(g.y) - 4.; //mirror
  g.x = abs(g.x) - 4.5;//mirror

  float ground = fBox(g+vec3(2.,0.,2.), vec3(0.25, 3.25, 2000000.75));


  // light
  vec4 lColor = vec4(0.,0.,1.,1.); 
  l.xy *= rot(radians(45.)); //rotate
  l.zy *= rot(radians(90.)); //rotate
  l.z = abs(l.x) - 5. ;//mirror
  pMod1(l.y, 1.);



  float light = fCylinder(l+vec3(1.6,0.9,2.1), 1.,1.); 

  //Pillars
  vec4 pColor = vec4(1., 0.5, 1., 1.); 
  p.z = abs(p.z) + 5.; //mirror
  p.x = abs(p.x) - cos(iTime)*.5; //mirror
  pMod1(p.z, 8.);
  p.xy *= rot(radians(90.));//rotate
  float pillar = fBox(p+vec3(0.,3.,1.5), vec3(2., 0.5, 0.)); 
  pillar = fOpUnionColumns(ground, pillar, 0.7, -1.);

  //Side 
  vec4 sColor = vec4(0.5, 0., 1., 1.0); 
  s.x = abs(s.x) - 6.; //mirror
  pMod1(s.z, 6.); 
  float side = fBox(s+vec3(3.,1., -5.), vec3(0.2, .5, 7.)); 
  side = fOpUnionStairs(ground, side,2., 4.); 

  shape.dist = min(ground, pillar);
  shape.dist = min(shape.dist, side); 

  shape.color = mix(shape.color, lColor, mixColors(light, shape.dist, 0.1));
  shape.color = mix(shape.color, pColor, mixColors(pillar, shape.dist, 0.5));
  shape.color = mix(shape.color, sColor, mixColors(side, shape.dist, 0.3)); 

  return shape; 
}


Shape map(vec3 c){
  Shape enviro = environment(c);
  return enviro;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;

  vec3 cam = vec3(0., 0., iTime*3.);
  cam.xy *= rot(radians(90.));
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.0001){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist;


  }
}
