float tweak(float x, float shift) {

    if (x < 0.5) {
        x += shift;
    } else if (x > 0.5) {
        x -= shift;
    }

    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel0, uv);

    float shift = sin(iTime); 
	col.r = tweak(col.r, shift);
	col.g = tweak(col.g, shift);
	col.b = tweak(col.b, shift);

    // Output to screen
    fragColor = col;
}
