void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv = -1.0+2.0*uv;
    //uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
    fragColor -= max(dot(uv*2.0-1.0,uv*2.0-1.0)-0.9,0.)/2.;
}

float scaleValue( vec2 uv, float scaleOffset ) {
    float scale = 0.;
    //float prog = iter == maxiter ? 0. : scaleValue(iter+1., maxiter, p*2.);
    for(int i = 0; i < 4; i++) {
        float power = pow(2., float(i));
        vec2 p = uv * power + vec2(floor(3.*float(i)/13.)/4.);
        p.x = (mod(p.x,0.) == 0. && p.x != 0.) ? 1. : mod(p.x, 1.);
        p.y = (mod(p.y,0.) == 0. && p.y != 0.) ? 1. : mod(p.y, 1.);
    	if(p.x <= 0.5 && p.y <= 0.5) scale += 0.25 + scaleOffset;
    	else if(p.x > 0.5 && p.y <= 0.5) scale += 0.50 + scaleOffset;
    	else if(p.x > 0.5 && p.y > 0.5) scale += 0.75 + scaleOffset;
    	else if(p.x <= 0.5 && p.y > 0.5) scale += 1. + scaleOffset;
        scale += float(i);
        scale += floor(float(i)/7.);
    	//scale = mod(prog + scale, 1.);
    }
    scale = mod(scale, 1.);
    if(scale == 0.) scale = 1.;
    return scale;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord / iResolution.xy;
    p = p*2.0-1.0;
    p.x *= iResolution.x/iResolution.y;
    p /= 1.618033;
    //vfx
    vec2 fc = vec2(cos(iTime*2.)/100., sin(iTime*2.)/100.);
    p -= fc*2.;
    vec3 col = vec3(0.,0.,0.);
    //col = vec3(scaleValue(p, iTime/16.));
    col.r = scaleValue(p+fc.xy,iTime/4.);
    col.g = scaleValue(p,iTime/4.);
    col.b = scaleValue(p+fc.yx,iTime/4.);
    fragColor = vec4(col,0.);

}
