void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = vec2(0, 0);

    xy.x = fragCoord.x / iResolution.x;
    xy.y = fragCoord.y / iResolution.y;

	/*vec4 blackToGreenGradient = vec4(0, xy.x, 0, 1);
    vec4 verticalGradient = vec4(0, xy.y, 0, 1);
    vec4 multiColorGradient = vec4(xy.x, xy.y, 0, 1);*/

    float r, g, b, a;
    r = (xy.x + xy.y)/2.;
    g = 0.;
    b = (xy.x - xy.y)*sin(iTime);
    a = 1.;

    vec4 diagonalGradient = vec4(r, g, b, a);
    fragColor = diagonalGradient;
}
