vec2 once(vec2 uv);	//vec2 twice(vec2 uv); vec2 thrice(vec2 uv); vec2 forths(vec2 uv); vec2 fifths(vec2 uv);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float faster=iTime*1.5;
	vec2 uv = (fragCoord.xy-4.0*iMouse.xy) / (4.0*iResolution.xx);
    vec2 uv2 = uv;

    int steps = int(mod(floor(faster),5.0));

	vec2 uv1 = once(uv);
	//if(steps==1) {	uv1 = twice(uv1);}

    //for (int i = 0; i < 5; i++)
    //{	  if(i == steps) {break;}
	//	   uv1 = once(uv1);      }

    if(steps > 0) {uv1=once(uv1);}
    if(steps > 1) {uv1=once(uv1);}
    if(steps > 2) {uv1=once(uv1);}
    if(steps > 3) {uv1=once(uv1);}

    if(steps == 4) {uv2=once(uv);}
    else {uv2=once(uv1);}


    float a=fract(faster);
    float b=1.0-a;
	fragColor = b*texture(iChannel1,uv1) +a*texture(iChannel1,uv2);

}


vec2 once(vec2 uv)
{	vec4 samp = texture(iChannel0,uv);
    return (2.0*(samp.xy) - vec2(1.0))/5.0;
}

