float maxlength = 100.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 sunpos = vec2(200.0, 200.0+sin(iTime)*100.0);
    vec2 uv = fragCoord.xy / iResolution.xy;

	fragColor = (texture(iChannel1, uv));

    if (length(fragCoord.xy-sunpos) < 10.0) {
    	fragColor = vec4(1.0,1.0,0.0,1.0);
    } else if (fragColor.a < 1.0) {
		vec2 p = fragCoord;
        vec2 dir = sunpos-p;
        float dist2 = abs(dir.x)+abs(dir.y);
        dir /= dist2;

        float len = 0.0;
        float dist = 0.0;

        for (int i = 0; i < 100; i++) {
            len = min((1.0-texture(iChannel0, p/iResolution.xy).a)*maxlength,(dist2-dist)+1.0);
            dist += len;

            if (len < 0.1 || dist > dist2) {
                break;
            }

            p += dir*len;
        }
        if (dist > dist2) {
            fragColor = vec4(0.5);
        }
       //if (length(sunpos-p) < 2.0) { //shows ray steps
       //     fragColor = vec4(0.0,1.0,1.0,1.0);
       //}

    }
    if (fragColor.r+fragColor.g+fragColor.b > 2.9) { //colors paint green
        fragColor = vec4(0.0,1.0,0.0,1.0);
    }

    //fragColor = (fragColor+texture(iChannel0, uv))/2.0; //shows buffer a
}

float maxlength = 100.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 pix = vec3(1.0/iResolution.xy,0.0);
    vec2 uv = fragCoord.xy/iResolution.xy;
    fragColor = texture(iChannel1, uv);

    if (fragColor.a < 1.0) {
        float num = max(max(max(texture(iChannel0, uv+pix.xz),
                                texture(iChannel0, uv+pix.zy)),
                                texture(iChannel0, uv-pix.xz)),
                                texture(iChannel0, uv-pix.zy)).a;
        fragColor = vec4(max(num-1.0/maxlength, 0.0));
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    fragColor = texture(iChannel1, uv);
    if (iFrame == 1) {
    	fragColor = vec4(0.0);
    }
    if (length(iMouse.xy-fragCoord.xy) < 10.0 && iMouse.z > 0.0) {
        fragColor = vec4(1.0);
    }
}
