void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord.xy - (iResolution.xy / 2.)) / 10.;

    float len = length(uv);

    vec2 xy = vec2(cos(len - iTime * 5.) * len, sin(len - iTime * 5.) * len);

    float a = length(xy - uv);

    if(a < 2.) {
        a = 1.5- a;
    }
    else {
        a = 0.;
    }

	fragColor = vec4(sqrt(len / 100.), a, 1, 1.0);
}
