float circled(vec2 uv, vec2 pos, float rad) {
	float d = length(pos - uv) - rad;
	return d;
}

float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

#define nCircles 128

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float radius = 0.025* iResolution.y;

    float anginc = (3.143*2.0)/float(nCircles);
	float ang = 0.0;
   	fragColor = vec4(0.1,0.8,0.25,1.0);
	vec4 col = vec4(0.4, 0.7,1.0,1.0);

    float func = 1000.0;

    for(int i=0; i<nCircles;i++)
    {
        float move = cos(iTime+ang*iTime*0.1);
        vec2 ps = center + vec2(cos(ang),sin(ang))*iResolution.x*0.26*move; 
        func = smin(func,circled(uv, ps, radius*abs(move)),70.0/iResolution.x);
        ang+=anginc;
    }

    fragColor = mix(fragColor,col*sign(-func),0.225);
}
