// colored variant of https://www.shadertoy.com/view/MttXRN
// tracker added to a slowed version of https://www.shadertoy.com/view/XldXRN

void mainImage( out vec4 O, vec2 U )
{	O = texture(iChannel0, U/iResolution.xy); }

#define T(x,y) texture(iChannel0, (U+vec2(x,y))/iResolution.rg)

void mainImage( out vec4 O, vec2 U )
{
    vec2   R = iResolution.xy;
    float Pi = 3.14159265359,
           t = 8.*iTime,
           e = 35./R.y, v;

    O = vec4(1);
    if ( iFrame<10 || ( iMouse.z>0. && U-.5==iMouse.xy ) ) 
        O = .5+.5*sin(U.x+917.*U.y+vec4(0,2,-2,0)), O.a=1.;        // mark dot with random hue

    if (T(0,0) != vec4(0))
        for (int j=-2; j<3; j++)
        	for (int i=-2; i<3; i++)
            { vec4 c = T(i,j); 
              if ( c.a==1. && c.rgb!=vec3(1) ) { O = c; break; }   // track dot
            }

// ------ code similar to https://www.shadertoy.com/view/XldXRN ------------------------------
	U = ( U+U - R ) /R.y * 8.  
         * mat2(sin(Pi/3.*floor(t/2./Pi) + Pi*vec4(.5,1,0,.5)));   // animation ( switch dir )

  	U.y /= .866; 
    U -= .5;   
    v = ceil(U.y);
    U.x += .5*v;                                                   // hexagonal tiling
	U.x +=  (1.-cos(t/2.)) * (mod(v,2.)-.5) ;                      // animation ( scissor )

    U = 2.*fract(U)-1.;                                            // dots
    U.y *= .866;
	O *= smoothstep(e,-e, length(U)-.6);
}
