#define M_PI 3.1415926535897932384626433832795

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos = (fragCoord / iResolution.xy - vec2(0.5, 0.5)) * 2.0;
    float dist = sqrt(pos.x * pos.x + pos.y * pos.y);

    float size = 2.0;
    float time = fract(iTime / 3.0);

    time *= 4.0;

    vec4 col;

    if(dist > time - size && dist < time) {
        col = texture(iChannel0, fragCoord / iResolution.xy * 1.0 + normalize(pos) * sin((dist - time + size) / size * M_PI));
        vec3 hsv = rgb2hsv(col.xyz);
        hsv.x -= sin((dist - time + size) / size * M_PI) * pow(sin(time) * 1.3, 3.0) * 40.0 * pow(hsv.y, 4.0);
        col = vec4(hsv2rgb(hsv), 0.0);
    } else {
        col = texture(iChannel0, fragCoord / iResolution.xy * 1.0);
    }

    fragColor = col;
}
