// engine color sub function
float mixRange(float x, float low, float hi) {
	return clamp((x - low) / (hi - low), 0.0, 1.0);
}

// engine color
vec3 blackBody(float temp) {
	vec3 c;
	c.r = mix(1.0, 0.6234, mixRange(temp, 6400.0, 29800.0));
	c.b = mix(0.0, 1.0, mixRange(temp, 2800.0, 7600.0));
	c.g = mix(mix(0.22, 0.976, mixRange(temp, 1000.0, 6600.0)), mix(0.976, 0.65, mixRange(temp, 6600.0, 29800.0)), floor(temp/29800.0 + 0.22148));
	return c;
}




vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 a = vec3(0.5, 0.5, 0.5);	
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.0, 0.2, 0.2);    

    vec2 uv = fragCoord.xy / iResolution.xy;

	// Engine setup
	vec3 engineColorDefault = (blackBody(1000.0 + 6000.0 * (uv.x * uv.x)));    

    vec3 engineColorTweakable = pow(palette((0.95 - uv.x * 0.75) * 0.75, a, b, c, d), vec3(2.2));

    fragColor = vec4(engineColorDefault, 1.0 );

    fragColor = (uv.y > 0.5) ? vec4(engineColorDefault, 1.0 ): vec4(1.0/(1.0 + engineColorTweakable * 8.0), 1.0 );
}
