void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Time varying pixel color
    vec4 result = vec4(1.0,1.0,1.0,1.0);
    vec2 translate = vec2(uv.x,uv.y);
    result = texture(iChannel0, uv);

    // Calculate modulo to decide even and odd row
    float div = 2.0;
    float num = floor(uv.y * 10.0);
    float odd = num - (div * floor(num/div));

    float t = mod(iTime,1.0);

    //Only do the odd number
    if( odd == 0.0){
        translate.x = translate.x - t;        
        result = texture(iChannel0,translate);
        if(translate.x<0.0){
            result.rgb = result.rgb * 0.0;
        }
    }
    else{
    	translate.x = translate.x + t;        
        result = texture(iChannel0,translate);
        if(translate.x>1.0){
            result.rgb = result.rgb * 0.0;
        }
    }

    // Output to screen
    fragColor = result;
}
