float t = 0.5;

vec2 circpos1 = vec2(0.45, 0.5);
float circsize = 0.1;
vec4 circolor1 = vec4(1.0,0.0,0.0,0.0);

vec2 circpos2 = vec2(0.55, 0.5);
vec4 circolor2 = vec4(0.0,1.0,0.0,0.0);

vec2 circpos3 = vec2(0.5, 0.4);
vec4 circolor3 = vec4(0.0,0.0,1.0,0.0);

vec4 bgcolor = vec4(1.0,1.0,1.0,1.0);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspectRatio = iResolution.x/iResolution.y;

    uv.x *= aspectRatio;

    circpos3.y += (sin(iTime*4.0)+1.0)/10.0;

    if(length(circpos1 - uv) < circsize)
        circolor1 = vec4(circolor1.rgb, 0.5);
    if(length(circpos2 - uv) < circsize)
        circolor2 = vec4(circolor2.rgb, 0.5);
    if(length(circpos3 - uv) < circsize)
        circolor3 = vec4(circolor3.rgb, 0.5);
    vec3 col = vec3(1.0,1.0,1.0);
    col = mix(bgcolor.rgb, circolor1.rgb, circolor1.a);
    col = mix(col.rgb, circolor2.rgb, circolor2.a);
    col = mix(col.rgb, circolor3.rgb, circolor3.a);

    //t = (sin(iTime*2.0)+1.0)/2.0;

    //vec4 nyan = texture(iChannel0, uv);

    //vec4 star = texture(iChannel1, uv);

    //vec3 col = mix(nyan.rgb, star.rgb, t);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
