#define COLOR vec4(1.0,0.0,0.0,0.5)
#define SPEED 0.25

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 fore = texture(iChannel0,uv); 

    vec4 back = texture(iChannel1,uv); 

    vec4 noise = texture(iChannel2,uv * 5.0); 

    vec4 light = COLOR;

    float offset;

    offset = sin(mod(iTime * SPEED,3.14 * 0.5));

    float a = offset * 3.0 - uv.x;

    a = a - noise.r;

    a = clamp (a,0.0,1.0);

	fragColor = back * (1.0 - a) + fore * a;

    //fragColor = noise;
}
