void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*4.0;

    float fft=texture(iChannel0,vec2(440.0/11025.0,0.6)).r;
    float a=        texture(iChannel0,vec2(200.0/11025.0,0.2)).r;
    float b=        texture(iChannel0,vec2(125.0/11025.0,1)).r;
    float c =		texture(iChannel0,vec2(1000.0/11025.0,1)).r;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*tan(iTime+fft*fft+uv.yxy+vec3(0,a*a+c,1.0-b*b));

    // Output to screen
    fragColor = vec4(col,1.0);
}
