//Colors
vec3 color1 = vec3 (0.4, 0.25, 0.15); //brown
vec3 color2 = vec3 (1.0, 0.0, 0.0); //red
vec3 color3 = vec3 (0.0, 0.0, 1.0); //blue

//House
vec2 houseCornerPoint = vec2 (0.3, 0.1);
vec2 houseDimensions = vec2 (0.4, 0.3);

//Chimney
vec2 chimneyCornerPoint; //changes based on houseCornerPoint
vec2 chimneyDimensions = vec2 (0.05, 0.1);

//Window1 (Left Window)
vec2 window1CornerPoint; //changes based on houseCornerPoint
vec2 windowDimensions = vec2 (0.05, 0.05);

//Window2 (Right Window)
vec2 window2CornerPoint; //changes based on houseCornerPoint

//Layers On/Off (1.0 = Layer On, 0.0 = Layer Off)
float houseLayer = 1.0;
float chimneyLayer = 1.0;
float windowsLayer = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized Pixel Coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Instantiate Layers
    vec4 Layer1 = vec4 ((uv.y + 1.0), (uv.y + 1.0) * 0.5 , 0.0, 1.0); //Background
    vec4 Layer2; //House
    vec4 Layer3; //Chimney
    vec4 Layer4; //Window1 (left window)
    vec4 window2; //(right window)

    //Aspect Ratio Correction
    float aspectRatio = iResolution.y / iResolution.x;
    uv.y *= aspectRatio;
    houseCornerPoint.y = houseCornerPoint.y * aspectRatio; //all other cornerpoints are based off the houseCornerPoint, so they are already corrected

    //House
    float houseInterpX = step(houseCornerPoint.x, uv.x) - step(houseCornerPoint.x + houseDimensions.x, uv.x);
    float houseInterpY = step(houseCornerPoint.y, uv.y) - step(houseCornerPoint.y + houseDimensions.y, uv.y);
    float houseInterp = houseInterpX * houseInterpY;
    Layer2 = (vec4 (color1, houseInterp));

    //Chimney
    chimneyCornerPoint = vec2 (houseCornerPoint.x, houseCornerPoint.y + houseDimensions.y);

    float chimneyInterpX = step(chimneyCornerPoint.x, uv.x) - step(chimneyCornerPoint.x + chimneyDimensions.x, uv.x);
    float chimneyInterpY = step(chimneyCornerPoint.y, uv.y) - step(chimneyCornerPoint.y + chimneyDimensions.y, uv.y);
    float chimneyInterp = chimneyInterpX * chimneyInterpY;
    Layer3 = (vec4 (color2, chimneyInterp));

    //Window1
    window1CornerPoint = vec2 (houseCornerPoint.x + (houseDimensions.x / 4.0), houseCornerPoint.y + (houseDimensions.y / 2.0));

    float window1InterpX = step(window1CornerPoint.x, uv.x) - step(window1CornerPoint.x + windowDimensions.x, uv.x);
    float window1InterpY = step(window1CornerPoint.y, uv.y) - step(window1CornerPoint.y + windowDimensions.y, uv.y);
    float window1Interp = window1InterpX * window1InterpY;
    Layer4 = (vec4 (color3, window1Interp));

    //Window2
    window2CornerPoint = vec2 (houseCornerPoint.x + (houseDimensions.x / 1.6), houseCornerPoint.y + (houseDimensions.y / 2.0));

    float window2InterpX = step(window2CornerPoint.x, uv.x) - step(window2CornerPoint.x + windowDimensions.x, uv.x);
    float window2InterpY = step(window2CornerPoint.y, uv.y) - step(window2CornerPoint.y + windowDimensions.y, uv.y);
    float window2Interp = window2InterpX * window2InterpY;
    window2 = (vec4 (color3, window2Interp));

    //Combine Windows into One Layer
   	Layer4 = mix(Layer4, window2, window2.a);

    //Turn Layers Off (if specified)  
    Layer2.a *= houseLayer;
    Layer3.a *= chimneyLayer;
    Layer4.a *= windowsLayer;  

    //Blending
    vec4 composite = mix(Layer1, Layer2, Layer2.a);
    composite = mix(composite, Layer3, Layer3.a);
    composite = mix(composite, Layer4, Layer4.a); 

    //Output to Screen
    fragColor = composite;
}
