void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 tx = (fragCoord.xy / iResolution.xy + vec2(-0.5, -0.5)) * 6.0;
    vec2 uv = tx * vec2(1, 2);
    float pi = 3.1416;

    float dx = tx.x + iTime;
    float hanning = (0.5 - 0.5 * (1.0 - cos(2.0 * pi * dx))) * 2.0;
    float fx = hanning * (sin(tx.x) / tx.x * 8.0 / 2.0 + 0.5);
    vec2 dir = vec2(tx.x, fx);

    float line = 1.0 - distance(dir, uv);

	fragColor = vec4(line);
}
