void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p1 = vec2(0.5);
    vec2 p2 = fragCoord.xy / iResolution.xy;
	float xDiff = p2.x - p1.x;
    float yDiff = p2.y - p1.y;
	float dist;

    if(mod(atan(yDiff, xDiff) * (30.0*10.0 / 3.1415), 20.0) < mod(1.0 * 1.0, 35.0)){
    dist = 0.0 ;   
    }
  	else
	dist =1.0;


	fragColor = vec4( dist,dist, dist,1.0);
}
