float map(vec3 p)
{

    vec3 q = fract(p) * 2.0 - 0.25;

    return length(q) - 1.0;

}

float trace(vec3 o,vec3 r)
{

    float t = 0.0;

    for(int i = 0; i < 16; i++)
    {

        vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;

    }

    return t;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 2.0 - 1.0;

    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 1.0));

    vec3 o = vec3(0.0, sin(iTime/4.)/pow(2., 6.), 3.0);

    float t = trace(o, r);

    float fog = 1.0 / (smoothstep(float(texture(iChannel0, vec2(1.1,0.7)).r), 0.0, 100.0) + t * t * sin(iTime));

    vec3 fc = vec3(fog*2.5*sign(uv.x)*0.5,
                   fog*2.5*sign(uv.x)*0.5,
                   fog*4.5*sign(uv.x)*0.5);

    fragColor = fog*sign(uv.x)/4. * vec4(5,5,9,0);

}
