void mainImage( out vec4 color, in vec2 pixCoords )
{
   	float PI = 3.14;
    float centerX = iResolution.x / 2.0 + cos(iTime*0.2) * 666.0 ;
    float centerY = iResolution.y / 3.6 + sin(iTime*0.2) * 666.0 ;
    float centerX1 = iResolution.x / 2.0 - cos(iTime*0.2) * 666.0 ;
    float centerY1 = iResolution.y / 3.0 - sin(iTime*0.2) * 666.0 ;
    float radius = 50.0 ;
    float lenght = sqrt(pow(centerX - pixCoords.x, 2.0) + pow(centerY - pixCoords.y, 2.00));
    float lenght1 = sqrt(pow(centerX1 - pixCoords.x, 2.0) + pow(centerY1 - pixCoords.y, 2.00));
    float sea1 = 80.0 + 20.0 * cos(iTime - pixCoords.x * 0.2 * 3.0/ 60.0)*cos(iTime * 0.4 * 20.0/ 10.0);
    float sea2 = 110.0 + 20.0 * cos(iTime + pixCoords.x * 0.2 * 4.0/ 55.0)*cos(iTime * 0.3 * 20.0/ 10.0);
    float sea3 = 140.0 + 20.0 * cos(iTime - pixCoords.x * 0.2 * 7.0/ 50.0)*cos(iTime * 0.4 * 20.0/ 10.0);
    float sea4 = 170.0 + 20.0 * cos(iTime + pixCoords.x * 0.2 * 9.0/ 45.0)*cos(iTime * 0.3 * 20.0/ 10.0);
    float sea5 = 200.0 + 20.0 * cos(iTime - pixCoords.x * 0.2 * 11.0/ 40.0)*cos(iTime * 0.4 * 20.0/ 10.0);
    float beach = 20.0 * cos (pixCoords.x * 0.2 /20.0);

    if (pixCoords.y < beach) {
        color = vec4(centerY / iResolution.y, centerY / iResolution.y, 0.0, 0.0);
        return;
        }

    if (pixCoords.y < sea1){
        color = vec4(0.0, 0.0, 0.1 * centerY / iResolution.y, 1.0); 
		return;
    }

    if (pixCoords.y < sea2){
        color = vec4(0.0, 0.0, 0.3 * centerY / iResolution.y, 1.0);
		return;
    }

    if (pixCoords.y < sea3){
        color = vec4(0.0, 0.0, 0.4 * centerY / iResolution.y, 1.0); 
		return;
    }

    if (pixCoords.y < sea4){
        color = vec4(0.0, 0.0, 0.5 * centerY / iResolution.y, 1.0); 
		return;
    }

    if (pixCoords.y < sea5){
        color = vec4(0.0, 0.0, 0.6 * centerY / iResolution.y, 1.0); 
		return;
    }

    if (lenght < radius){
        color= vec4(1.0, 1.0, 0.0, 1.0);
        return;
    }

    else color = vec4(0.0, 0.0, 1.0, 1.0);

    if (lenght1 < radius){
        color= vec4(0.8,0.8,0.8,0.8);
        return;
    }

    color = vec4 (0.05, 0.5 * centerY /  iResolution.y, 1.0 * centerY /  iResolution.y, 1.0);

   // color = texture(iChannel0, pixCoords/iResolution.xy); 
}
