void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2. - iResolution.xy)/iResolution.y * 5.;

	float a = length(uv*0.5) * sin(iTime * 0.2);

	uv *= mat2(cos(a), -sin(a), sin(a), cos(a));

	uv = abs(uv)-5.*cos(iTime * 0.2);

	uv = vec2(atan(uv.x, uv.y)/3.14159*3., length(uv));
	uv = abs(fract(uv)-.5);

	float b = sqrt(uv.x) + sqrt(uv.y);
	float c = sqrt(uv.x + uv.y);

	float s = b-c;

	fragColor = vec4(s);
}
