void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 m = iMouse.xy / iResolution.xy;
    vec2 m_l = vec2(iMouse.x - 1., iMouse.y) / iResolution.xy;
    vec2 m_r = vec2(iMouse.x + 1., iMouse.y) / iResolution.xy;

    //float dist = length(uv - m);			// Circle
    float dist_v = length(uv.x - m.x);		// Vertical Line
    float dist_v_l = length(uv.x - m_l.x);	// Vertical Line
    float dist_v_r = length(uv.x - m_r.x);	// Vertical Line

    float dist_h = length(uv.y - m.y);// Horizontal Line

    float radius = .001;
    float smoothv = .0005;

    vec4 col_v = vec4(smoothstep(radius, radius + smoothv, dist_v));
    vec4 col_v_l = vec4(1. - smoothstep(radius, radius + smoothv, dist_v_l), 0., 0., 1.);
    vec4 col_v_r = vec4(0., 1. - smoothstep(radius, radius + smoothv, dist_v_r), 0., 1.);

    vec4 col_h = vec4(smoothstep(radius, radius + smoothv, dist_h));

    fragColor = col_v * col_h;

	//fragColor = mix(col_v_l, col_v_r, col_v_r);
    //fragColor = mix(mix(col_v_l, col_v_r, col_v_r), col_v, 1. - col_v);
}
