float s=0.95,a=0.006,scale=3.;
float d_box(vec2 q){float d=max(abs(q.x),abs(q.y));return smoothstep(s,s+a,d)+smoothstep(s-a,s-a-a,d);}
float d_sph(vec2 q,float s){float d=pow(pow(abs(q.x),3.0)+pow(abs(q.y),3.0),1./3.);return smoothstep(s,s+a,d)+smoothstep(s-a,s-a-a,d);}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    vec3 col=vec3(1.0)*d_box(q);
    float iter = mod(floor(iTime),7.0);
    for(int i=0;i<4;++i)
    {
        if(i<int(iter+0.5))
        {
            q=abs(q);
            if(q.x>q.y)q.xy=q.yx;
            s*=1.0/scale;
            col*=d_sph(q,s);
            q-=s*(scale-1.);
            if(q.x<-0.5*s*(scale-1.0))q.x+=s*(scale-1.0);
        }
        else
        {
            col*=d_sph(q,fract(iTime)*s/scale);
        }
    }
    fragColor.xyz=col;
}
