bool partOfLeaf(vec2 lc, vec2 uv) {
    //vec2 p = vec2(abs(lc.x - uv.x), abs(lc.y-uv.y));
    vec2 p = lc - uv;
    return (p.x*p.x* exp(30.0*p.y)+p.y*p.y/4.0) <= 0.0002;
   // return sqrt(p.x * p.x + p.y * p.y) < 1.0;
}

bool closeToAnyPoint(vec2 uv) {
    for (int i = 0; i < POINTS; i++) {
        vec2 pt = texelFetch(iChannel0, ivec2(i, 0), 0).xy;

        if (distance(uv, pt) < 0.002) {
            return true;
        }
    }

    return false;
}

int closeToWhichLeaf(vec2 uv) {
	//return 10;
    //return 0;
    for (int i = 0; i < POINTS; i++) {
        vec2 pt = texelFetch(iChannel0, ivec2(i, 0), 0).xy;

        if (partOfLeaf(pt, uv)) {
            return i;
        }
    }

    return -1;
}

