vec4 colorForPoint(int id) {
    return texelFetch(iChannel0, ivec2(id, 1), 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float size = max(iResolution.x, iResolution.y);
    vec2 uv = fragCoord/size;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(col,1.0);

    if (closeToAnyPoint(uv)) {
        fragColor = vec4(0.0,1.0,1.0,1.0);
    } else {
        fragColor = colorForPoint(closeToWhichLeaf(uv));
    	//fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }


}

float haltonSeq(int b, int i) {
	float r = 0.0;
    float f = 1.0;
    while (i > 0) {
        f = f / float(b);
        r = r + f * float(i % b);
        i = int(floor(float(i) / float(b)));
    }
    return r;
}

float greeny(float x) {
    return x / 2.0 + 1.0 / 2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (int(fragCoord.y) == 0) {
        if (int(fragCoord.x) < POINTS) {
        	fragColor = vec4(
            	haltonSeq(2, int(fragCoord.x) * 409),
                haltonSeq(3, int(fragCoord.x) + 19),
               	0.0,
              	0.0);
           	return;
        }
    }
    if (int(fragCoord.y) == 1) {
        if (int(fragCoord.x) < POINTS) {
        	fragColor = vec4(
            	haltonSeq(5, int(fragCoord.x) * 409) / 3.0,
                greeny(haltonSeq(7, int(fragCoord.x) * 409)),
               	haltonSeq(11, int(fragCoord.x) * 409) / 3.0,
              	1.0);
           	return;
        }
    }

    fragColor = vec4(0.0, 0.0, 0.0, 0.0);


}
