void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    fragColor = texture(iChannel0,uv);
}

//Horizontal

const float blur_amount = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
	float blurSize = 1.0/iResolution.x * blur_amount;
	vec4 sum = vec4(0.0);
	sum += texture(iChannel0, vec2(uv.x - 4.0*blurSize, uv.y)) * 0.0162162162;
	sum += texture(iChannel0, vec2(uv.x - 3.0*blurSize, uv.y)) * 0.0540540541;
	sum += texture(iChannel0, vec2(uv.x - 2.0*blurSize, uv.y)) * 0.1216216216;
	sum += texture(iChannel0, vec2(uv.x -     blurSize, uv.y)) * 0.1945945946;
	sum += texture(iChannel0, vec2(uv.x,                uv.y)) * 0.227027027;
	sum += texture(iChannel0, vec2(uv.x +     blurSize, uv.y)) * 0.1945945946;
	sum += texture(iChannel0, vec2(uv.x + 2.0*blurSize, uv.y)) * 0.1216216216;
	sum += texture(iChannel0, vec2(uv.x + 3.0*blurSize, uv.y)) * 0.0540540541;
	sum += texture(iChannel0, vec2(uv.x + 4.0*blurSize, uv.y)) * 0.0162162162;
	fragColor = sum;
}

//Vertical

const float blur_amount = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
	float blurSize = 1.0/iResolution.x * blur_amount;
	vec4 sum = vec4(0.0);
	sum += texture(iChannel0, vec2(uv.x, uv.y - 4.0*blurSize)) * 0.0162162162;
	sum += texture(iChannel0, vec2(uv.x, uv.y - 3.0*blurSize)) * 0.0540540541;
	sum += texture(iChannel0, vec2(uv.x, uv.y - 2.0*blurSize)) * 0.1216216216;
	sum += texture(iChannel0, vec2(uv.x, uv.y -     blurSize)) * 0.1945945946;
	sum += texture(iChannel0, vec2(uv.x, uv.y               )) * 0.227027027;
	sum += texture(iChannel0, vec2(uv.x, uv.y +     blurSize)) * 0.1945945946;
	sum += texture(iChannel0, vec2(uv.x, uv.y + 2.0*blurSize)) * 0.1216216216;
	sum += texture(iChannel0, vec2(uv.x, uv.y + 3.0*blurSize)) * 0.0540540541;
	sum += texture(iChannel0, vec2(uv.x, uv.y + 4.0*blurSize)) * 0.0162162162;
	fragColor = sum;
}
