void mainImage( out vec4 Vi, in vec2 fragCoord )
{

vec2 uv = (fragCoord/iResolution.xy)*vec2(1.777,1.)- vec2(0.3885,0.);
float M = clamp(ceil(uv.x)*ceil(1.-uv.x),0.,1.);
int L = 5;
float V = 1.;
int T = int(fract(iTime*.2)*float(L))+1;

for(int i =0;i<T;i++)
{
	vec2 P = abs(fract(uv*pow(3.,float(i)))-.5);
	V *= 1.-clamp(round(min(P.x,P.y)*3.),0.,1.);
}
V*=M;

    // Output to screen
    Vi = vec4(V,V,V,1.);
}
