#define intensity 0.02

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    vec2 offset = texture(iChannel0, st).rg; 
    vec2 pos = st + offset * intensity;

    fragColor = texture(iChannel0, st);
}

#define scale 8.0
#define speed 1.0

vec3 random2(vec3 st){
    st = vec3( dot(st,vec3(127.1,311.7,-12.6)),
              dot(st,vec3(269.5,183.3,17.9)),
              dot(st,vec3(56.8,103.3,80.5)));
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

float noise(vec3 st) {
    vec3 i = floor(st);
    vec3 f = fract(st);

    vec3 u = (1.0-cos(f*3.14159))*0.5;

    return mix(mix( mix( dot( random2(i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.,0.0) ),
                     dot( random2(i + vec3(1.0,0.0, 0.0) ), f - vec3(1.0,0.0, 0.0) ), u.x),
                mix( dot( random2(i + vec3(0.0,1.0, 0.0) ), f - vec3(0.0,1.0, 0.0) ),
                     dot( random2(i + vec3(1.0,1.0, 0.0) ), f - vec3(1.0,1.0, 0.0) ), u.x), u.y),
              mix( mix( dot( random2(i + vec3(0.0,0.0, 1.0) ), f - vec3(0.0,0.0, 1.0) ),
                     dot( random2(i + vec3(1.0,0.0, 1.0) ), f - vec3(1.0,0.0, 1.0) ), u.x),
                mix( dot( random2(i + vec3(0.0,1.0, 1.0) ), f - vec3(0.0,1.0, 1.0) ),
                     dot( random2(i + vec3(1.0,1.0, 1.0) ), f - vec3(1.0,1.0, 1.0) ), u.x), u.y), u.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    vec2 pos = vec2(st*scale);

    float h = noise(vec3(pos.xy, iTime * speed)) * 0.5 + 0.5;

    fragColor = vec4(h);
}

