//Jamie Pendergast
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (-1.0 + 2. * fragCoord/iResolution.xy) * 2.;
    uv.x = fract(uv.x) * 2. - 1.;
    vec2 a = uv;
   	vec2 b = -uv + vec2(0.,1.);
    float t = dot(uv,a) / dot(uv,b);
    fragColor = vec4(abs(t));
}
