// Thanks Fabrice :)
void mainImage(out vec4 o,vec2 g){
    vec2 R = iResolution.xy,
         p = (g+g-R)/R.y,
         u = fract(p*4.)- .5;
    u = abs( u * mat2(cos( ceil(p.x*4.)/4. + iTime/2. + vec4(0,33,11,0))) );
    p = abs(p);
    o += p.x < 1.2 && p.y < .7 ? smoothstep(16./R.y,0.,.1-max(u.x-.001, u.y-.2)) : 1.;}

