/* Based on https://www.shadertoy.com/view/XtlGRn
 * by zephyr mann
 */
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float DISPLACEMENT = 0.05;
    const float SPACING = 0.005;

    vec2 uv = fragCoord.xy / iResolution.xy;

    if(uv.x > 0.5 - SPACING && uv.x < 0.5 + SPACING)
    {
        fragColor = vec4(0.0);
    }
    else
    {
        if(uv.x > 0.5 + SPACING)
        { 
          uv.x -= 0.5;
          float d = texture(iChannel1, uv, 0.0).r;
          uv.x -= d * DISPLACEMENT;
        }      

        float x = uv.x * 2.0;
        float y = uv.y * 1.0;
        vec3 rgb = texture( iChannel0, vec2(x,y), -100.0 ).yxz;

        fragColor = vec4(rgb,1.0);
    }
}
