float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,iTimeDelta))) * 43758.5453123);
}


float norm( float val ){
	return min(max( val, 0.0 ), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 xy = fragCoord.xy;

    vec3 clr = vec3(0.0, 0.0, 0.0);

    vec2 posA = vec2( 250.0, 150.0 );
    vec2 posB = vec2( iMouse.x, iMouse.y ); //vec2( iTimeDelta + 100.0, 100.0 );

    float radA = 100.0;
    float radB = 110.0;

    float distA = distance(xy, posA);
    float distB;

    if(distA < radA){

        clr.r = 1.0-smoothstep( 85.0, radA, distA );
        clr += (random( xy )-.5)/6.0;

        // "varjo"

		distB = distance(xy, posB);
        if( distB < radB ){
            clr-=1.0-smoothstep( 90.0 + 15.0 * smoothstep( 0.0, 60.0, distA ), radB, distB );
        }
    }
	fragColor = vec4(clr, 1.0);
}
