vec4 cclr;
float ar;
vec2 uv;
vec2 center;

#define BALLSIZE 0.2

void composite(vec4 clr) {
    cclr = vec4(clr.rgb * clr.a + cclr.rgb * cclr.a * (1.0 - clr.a), 1.0);
}

void drawparticle(vec2 pos, float radius, vec3 color) {
    float dist = distance(pos, uv);
    if(dist < radius) {
        composite(vec4(color, smoothstep(radius, 0.0, dist)));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    cclr = vec4(0.0, 0.0, 0.0, 1.0);
    ar = iResolution.x / iResolution.y;
    uv = vec2(fragCoord.x / iResolution.x * ar, fragCoord.y / iResolution.y);
    center = vec2(ar / 2.0, 0.5);

    drawparticle(vec2(center.x + cos(iTime * 1.0) * (0.25 + 0.05 * sin(iTime * 0.4)), center.y + sin(iTime * 1.0) * (0.25 + 0.05 * sin(iTime * 0.4))),
                 BALLSIZE, vec3(1.0, 0.5, 0.25));
    drawparticle(vec2(center.x + cos(iTime * 2.0) * (0.25 + 0.05 * sin(iTime * 0.3)), center.y + sin(iTime * 2.0) * (0.25 + 0.05 * sin(iTime * 0.3))),
                 BALLSIZE, vec3(0.25, 0.5, 1.0));
    drawparticle(vec2(center.x + cos(iTime * 3.0) * (0.25 + 0.05 * sin(iTime * 0.2)), center.y + sin(iTime * 3.0) * (0.25 + 0.05 * sin(iTime * 0.2))),
                 BALLSIZE, vec3(0.25, 0.75, 0.25));
    drawparticle(vec2(center.x + cos(iTime * 4.0) * (0.25 + 0.05 * sin(iTime * 0.1)), center.y + sin(iTime * 4.0) * (0.25 + 0.05 * sin(iTime * 0.1))),
                 BALLSIZE, vec3(0.75, 0.75, 0.25));

    fragColor = cclr;

}
