vec4 lerp(vec4 a,vec4 b, float t)
{
    t = clamp(t, 0.0, 1.0);
    return clamp(t * a + (1.0-t) * b, 0.0, 1.0);
}

vec4 stepped(vec4 p, float t)
{
    return vec4(step(p.r, t), step(p.g, t), step(p.b, t), 1.0);
}

vec2 quantize(vec2 uv, float w, float h)
{
    return floor(uv * vec2(w, h)) / vec2(w, h);
}

vec4 metalize(sampler2D from, sampler2D to, vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 f = texture(from, uv);
    return texture(to, f.xy);
}

vec4 old_tv(sampler2D tex, vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float segs = iResolution.y / 10.0;
    float ts = sin((fragCoord.y * iTime) / segs) / (iResolution.x/10.0);
    uv.x += ts;    
    return texture(tex, uv);
}

vec4 stepped_blue(sampler2D tex, vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float t = (1.0 + cos(iTime)) / 2.0;
    vec2 qt = quantize(vec2(t, t), 50.0, 50.0);
    uv = quantize(uv, 10.0 + qt.x * 1000.0, 10.0 + qt.y * 1000.0);
    vec4 pix = texture(tex, uv);
    return stepped(pix, 0.3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = (1.0 + cos(iTime)) / 2.0;
    if(t < 0.3)
    {
    	fragColor = stepped_blue(iChannel0,fragCoord);    
    }
    else if(t < 0.6)
    {
        fragColor = metalize(iChannel0, iChannel0,fragCoord);
    }
    else
    {
        fragColor = old_tv(iChannel0,fragCoord);
