// Specific shaders stolen from
// ROT FUNCTIONS  : https://www.shadertoy.com/view/XsSSzG


const float MAX_TRACE_DISTANCE = 100.0;           // max trace distance
const float INTERSECTION_PRECISION = .001;        // precision of the intersection
const int NUM_OF_TRACE_STEPS = 120;


const float pi = 3.14159;

vec4 spherePos[8];
vec3 sphereRot[8];


// ROTATION FUNCTIONS TAKEN FROM
//https://www.shadertoy.com/view/XsSSzG
mat3 xrotate(float t) {
	return mat3(1.0, 0.0, 0.0,
                0.0, cos(t), -sin(t),
                0.0, sin(t), cos(t));
}

mat3 yrotate(float t) {
	return mat3(cos(t), 0.0, -sin(t),
                0.0, 1.0, 0.0,
                sin(t), 0.0, cos(t));
}

mat3 zrotate(float t) {
    return mat3(cos(t), -sin(t), 0.0,
                sin(t), cos(t), 0.0,
                0.0, 0.0, 1.0);
}


vec4 texCube( sampler2D sam, in vec3 p, in vec3 n, in float k )
{
	vec4 x = texture( sam, p.yz *.4);
	vec4 y = texture( sam, p.zx *.4);
	vec4 z = texture( sam, p.xy *.4 );
    vec3 w = pow( abs(n), vec3(k) );
	return (x*w.x + y*w.y + z*w.z) / (w.x+w.y+w.z);
}



float sdPlane( vec3 p )
{

    //float f = fbm( p.xy );
    //f *= fbm( p.yz );
    //f = 5. * smoothstep( abs(f) , 0.4 , 0.8 );
	return p.y;// + sin( p.x * 1. * p.z ) * .002; //- (f * .4); //(abs( f) * .3)/ max( 1. , pow( length( p ), 1.));

}


float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}


mat3 fullRotate( vec3 r ){

   return xrotate( r.x ) * yrotate( r.y ) * zrotate( r.z );

}

float rotatedPlane( vec3 p , vec3 rot ){

    vec3 q = fullRotate( rot ) * p;
    return sdPlane( q );


}


//----
// Camera Stuffs
//----
mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void doCamera( out vec3 camPos, out vec3 camTar, in float time, in float mouseX )
{
    float an = 0.3 + 10.0*mouseX;
	camPos = vec3(3.5*sin(an),1.0,3.5*cos(an));
    camTar = vec3(0.0,0.0,0.0);
}


// checks to see which intersection is closer
// and makes the y of the vec2 be the proper id
vec2 opU( vec2 d1, vec2 d2 ){

	return (d1.x<d2.x) ? d1 : d2;

}


//--------------------------------
// Modelling 
//--------------------------------
vec2 map( vec3 pos ){  

   	vec2 res = vec2( 100000. , -1000. );

    vec2 res2 = vec2( rotatedPlane( (pos - vec3( 0., -2. , 0.)) , vec3( .5 , 0. , 0. )) ,  0.);
   	res = opU( res ,  res2 );

    res2 = vec2( rotatedPlane( (pos - vec3( 0., -2. , 0.)) , vec3( -.5 , 0. , 0. )) ,  0.);
   	res = opU( res ,  res2 );

    res2 = vec2( rotatedPlane( (pos - vec3( 0., -2. , 0.)) , vec3( 0. , 0. , -0.5 )) ,  0.);
   	res = opU( res ,  res2 );


    //vec2 res = vec2( 10. , -1. );

    res2 = vec2( sdSphere( (pos - vec3( 0.)) , .5 ) ,  1.);
   	res = opU( res ,  res2 );

    res2 = vec2( sdSphere( (pos - vec3( 1.)) , .7 ) ,  2.);
   	res = opU( res ,  res2 );

    res2 = vec2( sdSphere( (pos - vec3( -1. , 1. , -1.)) , .7 ) ,  3.);
   	res = opU( res ,  res2 );

    res2 = vec2( sdSphere( (pos - vec3( -1. , 0. , 1.)) , .7 ) ,  4.);
   	res = opU( res ,  res2 );

    res2 = vec2( sdSphere( (pos - vec3( 1. , 0. , -1.)) , .7 ) ,  5.);
   	res = opU( res ,  res2 );

   	return res;

}


// Calculates our intersection by stepping through the
// map function, and returning both the object hit and the distance
// it is hit at
vec2 calcIntersection( in vec3 ro, in vec3 rd ){


    float h =  INTERSECTION_PRECISION*2.0;
    float t = 0.0;
	float res = -1.0;
    float id = -1.;

    for( int i=0; i< NUM_OF_TRACE_STEPS ; i++ ){

        if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;
	   	vec2 m = map( ro+rd*t );
        h = m.x;
        t += h;
        id = m.y;

    }

    if( t < MAX_TRACE_DISTANCE ) res = t;
    if( t > MAX_TRACE_DISTANCE ) id =-1.0;

    return vec2( res , id );

}

// Calculates the normal by taking a very small distance,
// remapping the function, and getting normal for that
vec3 calcNormal( in vec3 pos ){

	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}


float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<50; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 20.*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );

}


float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.612*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.5;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    for( int i =0; i < 8; i++ ){

        float x = 1. * cos(iTime *.13 * (float( i )+2.));
        float y = 1. * sin(iTime * .075 * (float( i )+4.));
        float z = 1. * sin(iTime * .1 * (float( i )+3.3));
        float r = .1 * ( sin( iTime * .1  *( float( i) +1.))+2.);

        spherePos[i] = vec4( x , y , z , r );


        sphereRot[i] = vec3( 
            (sin( iTime  * (float(i)+2.) * .4)) ,
            (sin( iTime  * (float(i)+8.2) * .2)) ,
            (sin( iTime  * (float(i)+3.6) * .01))
        );

    }




    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;
    //vec2 m = vec2( 1.1 +( sin( iTime * 0.4 ) + sin( iTime * 0.01 )) * .04 , 0. );

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m.x );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,1.5 + abs(sin( iTime * .01 ) + sin( iTime * .0315 ))) ); // 2.0 is the lens length

    vec2 res = calcIntersection( ro , rd  );


  //  vec3 pat = texCube( iChannel0, 0.5*pos, nor, 10.0 ).xyz;
    vec3 col = vec3( 0. );//vec3( .8 , .8 , .8 ); 


    vec3 colors[5];
    colors[0] = vec3( 1. , 0.8 , 0.8 );
    colors[1] = vec3( 0.8 , 1. , 0.8 );
    colors[2] = vec3( 0.8 , 0.8 , 1. );
    colors[3] = vec3( 1. , 1. , 0.8 );
    colors[4] = vec3( 1. , 0.8 , 1. );

    //vec3 col = texCube( iChannel0, ro + rd * 1. , rd , 1.0 ).xyz;
    // If we have hit something lets get real!
    if( res.y > -.5 ){

    	vec3 pos = ro + rd * res.x;
    	vec3 nor = calcNormal( pos );

        for( int i = 0; i < 5; i++ ){
            if( i ==int( res.y )-1 ){
        	col = colors[ i ] * .5;
            }
        }
        if( int(res.y) == 0 ){

        	col = vec3(.3);

        }
        vec3 refl = reflect( rd , nor );
        vec2 res2 = calcIntersection( pos + refl * .01 , refl  );


        //col += vec3( res2.y / 3. , 0. , 0. );

       // col = nor * .5 + .5;
        if( res2.y > -.5 ){

            for( int i = 0; i < 5; i++ ){
                if( i ==int( res2.y )-1 ){
                	col *= colors[ i ] * .5;
                }
            }

            if( int(res2.y) == 0 ){

                col *= vec3(1.);

            }
            vec3 pos2 = pos + refl * res2.x;
    		vec3 nor2 = calcNormal( pos2 );
          //  col = vec3( 0. , 1. , 0. );

            vec3 refl2 = reflect( nor , nor2 );
        	vec2 res3 = calcIntersection( pos2  + refl2 * .01 , refl2  );

            if( res3.y > -.5 ){

                for( int i = 0; i < 5; i++ ){
                    if( i == int( res3.y )-1 ){
                        col *= colors[ i ] * .5;
                    }
                }

                if( int(res3.y) == 0 ){

                	col *= vec3(1.);

            	}

                vec3 pos3 = pos + refl * res3.x;
                vec3 nor3 = calcNormal( pos3 );
              //  col = vec3( nor3 * .5 + .5 );

            }


        }


        //col = nor * .5 + .5;


    }

    // apply gamma correction
    col = pow( col, vec3(0.4545) );

    fragColor = vec4( col , 1. );

}
