// [2TC 15] Melt Down
// Copyleft {c} 2015 Michael Pohoreski
// Chars: 212 Chars

#define D 10. // duration = 10 seconds

float pour( float y )
{                        
//  float t = min( 1.,     iTime    / D), // one-time
    float t = min( 1., mod(iTime,D) / D), // repeat
          d = (y - t);                         
    return (y > t) ? t - d*d/D : y; // may need to switch to '+' if your Y axis is flipped                               
}

void mainImage( out vec4 f, in vec2 p ) {                                  
    vec2 uv = p / iResolution.xy;              
    uv.y = pour( 1. - uv.y );               
    f = texture( iChannel0, uv );
}
