float s( float b ) {
    return abs( b / 2. - mod(  iTime * 2e1, b ) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float a = 1e1, d, m = 0.;
	vec2 p = fragCoord.xy / a;
    for ( float i = 1e2 ; i < 2e2 ; i++ ) {
        d = distance( p, vec2( s( i ), s( i + 1. ) ) );
        if ( a > d ) {
            a = d;
            m = i;
        }
    }
    vec3 c;
	for ( int i = 0 ; i < 3 ; i++ ) {
        m /= 3.;
		c[ i ] = fract( m );
	}
    fragColor.rgb = clamp( c / a, 0., 1. );
}
